import React from "react";
import Login from "./Login";

const hour = new Date().getHours()


const isLoggedIn= true;
function renderConditionally(){
  if(isLoggedIn===false){
    return <h1>Hello</h1>
  }else{
    return <Login />
  }
}




function App() {
  return (
    <div className="container">
     {/* {renderConditionally()} */}
     {isLoggedIn === true?<h1>Hello You sucessfully logged in!</h1>:<Login />}
    </div>
  );
}

export default App;
